  CREATE OR REPLACE FORCE EDITIONABLE VIEW "NCIPROJECT"."BOOKINGS_V" ("ID", "NAME", "DEPARTMENT", "START_TS", "END_TS", "STATUS", "RESOURCE_TYPE", "CSS_CLASS", "RESOURCE_ID", "RESOURCE_CODE", "USER_ID", "USERNAME", "FULL_NAME", "USER_DEPARTMENT", "CHECKEDIN", "CHECKIN", "LOCATION", "CAL_START_TS", "CAL_END_TS", "NOTES", "INVITED_ATTENDEES") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select booking_id id, name||
  case when RESOURCE_type = 'Equipment' then '' else ' ('||capacity||')'end name, r.department, START_TS, END_TS,
  case when (CURRENT_TIMESTAMP between  START_TS  and  END_TS) and checkin is not null then 'In Use' else status end STATUS,RESOURCE_type,
  case RESOURCE_type
           when 'Desk'    then 'apex-cal-green'
           when 'Meeting Room' then 'apex-cal-yellow'
           when 'Lecture Hall'  then 'apex-cal-red'
           when 'Equipment' then 'apex-cal-silver'
       end as css_class , r.RESOURCE_ID,r.code RESOURCE_code, u.user_id , u.username, u.full_name, u.department user_department, checkin checkedin, 
       case when (CURRENT_TIMESTAMP between  START_TS - INTERVAL '15' MINUTE and  END_TS) and checkin is null then 'Click here to Check in' else '' end checkin, location,
       to_char(START_TS,'YYYY-MM-DD')||'T'|| to_char(START_TS,'HH24:MI:SS') cal_start_TS, to_char(END_TS,'YYYY-MM-DD')||'T'|| to_char(END_TS,'HH24:MI:SS') cal_END_TS, b.notes , (select count(*) from BOOKING_ATTENDEES a where b.booking_id = a.booking_id) invited_attendees
  from bookings B, resources r, campus_users u
  where b.RESOURCE_ID = r.RESOURCE_ID
  and b.user_id = u.user_id
  --and status = 'CONFIRMED'
  order by START_TS desc
;
